package forloop;

public class IfElseIf90_100 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=90;i<=100;i++) {
			if(i<95)
				System.out.println(i +" is less than 95");
			else if(i==95)
				System.out.println(i+" is equal to 95");
			else
				System.out.println(i+" is greater than 95");
		}

	}

}
